function randomFrom(array) {
  return array[Math.floor(Math.random() * array.length)];
}

function getRandomDate(start, end) {
  return new Date(
    start.getTime() + Math.random() * (end.getTime() - start.getTime()),
  );
}

function generateEmployee() {
  const firstName = randomFrom(firstNames);
  const lastName = randomFrom(lastNames);
  const street = randomFrom(streetNames);
  const stateData = randomFrom(usStates);
  const zipCode = String(Math.floor(10000 + Math.random() * 90000));

  const age = Math.floor(18 + Math.random() * (64 - 18));
  const birthDate = new Date();
  birthDate.setFullYear(birthDate.getFullYear() - age);
  birthDate.setMonth(Math.floor(Math.random() * 12));
  birthDate.setDate(Math.floor(Math.random() * 28) + 1);

  const startDate = getRandomDate(
    new Date(birthDate.getFullYear() + 18, 0, 1),
    new Date(),
  );

  const departments = [
    "Sales",
    "Marketing",
    "Engineering",
    "Human Resources",
    "Legal",
  ];

  return {
    firstName,
    lastName,
    dateOfBirth: birthDate.toISOString().split("T")[0],
    startDate: startDate.toISOString().split("T")[0],
    street,
    city: stateData.capital,
    state: stateData.abbr,
    zipCode,
    department: randomFrom(departments),
  };
}

function updateCounter() {
  const data = JSON.parse(localStorage.getItem("employees") || "[]");
  document.getElementById("counter").textContent = data.length;
}

function addEmployees() {
  const data = JSON.parse(localStorage.getItem("employees") || "[]");
  for (let i = 0; i < 50; i++) {
    data.push(generateEmployee());
  }
  localStorage.setItem("employees", JSON.stringify(data));
  updateCounter();
}

function clearDatabase() {
  localStorage.removeItem("employees");
  updateCounter();
}

updateCounter(); // Initial counter update on load
